Widget stack(var text, var color, var image) {
  return Padding(
    padding: const EdgeInsets.all(10.0),
    child: Stack(
      children: [
        Container(
          height: 120,
          width: 75,
          decoration: BoxDecoration(
              border: Border.all(width: 0.3, color: MyColors.black),
              borderRadius: BorderRadius.circular(50),
              color: color,
              boxShadow: [
                BoxShadow(
                    color: MyColors.grey,
                    offset: Offset(3.0, 3.0),
                    blurRadius: 5.0,
                    spreadRadius: 0.5),
              ]),
        ),
        Positioned(
          top: 5,
          left: 7,
          //bottom: 9,
          child: Column(
            mainAxisAlignment: MainAxisAlignment.spaceEvenly,
            children: [
              CircleAvatar(
                radius: 30,
                foregroundImage: NetworkImage(image),
              ),
              SizedBox(
                height: 15,
              ),
              Text(text)
            ],
          ),
        ),
      ],
    ),
  );
}

Widget box(
  var text,
  var image,
) {
  return Padding(
    padding: const EdgeInsets.only(right: 15.0),
    child: Container(
      height: 200,
      width: 180,
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          ClipRRect(
            borderRadius: BorderRadius.circular(15),
            child: Image.network(
              image,
              fit: BoxFit.fill,
              width: 180,
              height: 100,
            ),
          ),
          Padding(
            padding: const EdgeInsets.symmetric(vertical: 12.0, horizontal: 14),
            child: Text(
              text,
              style: TextStyle(fontSize: 25, fontWeight: FontWeight.w900),
            ),
          ),
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 14),
            child: Row(
              children: [
                CircleAvatar(
                  radius: 15,
                  foregroundImage: NetworkImage(
                      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSV8ZLVPFoYR_fqP7XahB5swaQuXmEJ6jb2Nw&usqp=CAU"),
                ),
                Text(
                  "   KW Khan",
                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.w700),
                ),
              ],
            ),
          )
        ],
      ),
      decoration: BoxDecoration(
          border: Border.all(width: 0.3, color: MyColors.black),
          borderRadius: BorderRadius.circular(15),
          color: MyColors.white,
          boxShadow: [
            BoxShadow(
                color: MyColors.grey,
                offset: Offset(3.0, 3.0),
                blurRadius: 5.0,
                spreadRadius: 0.5),
